$.tablesorter.addWidget({id:'staticRow',format:function(table){if(typeof this.hasSorted=='undefined'){this.hasSorted=true;$('tbody .static',table).each(function(){$(this).data('tableindex',$(this).index());});}else{var hasShuffled=true;while(hasShuffled){hasShuffled=false;$('tbody .static',table).each(function(){var targetIndex=$(this).data('tableindex');if(targetIndex!=$(this).index()){hasShuffled=true;var thisRow=$(this).detach();var numRows=$('tbody tr',table).length;if(targetIndex>=numRows){thisRow.appendTo($('tbody',table));}else if(targetIndex==0){thisRow.prependTo($('tbody',table));}else{thisRow.insertBefore($('tbody tr:eq('+targetIndex+')',table));}}});}}}});